import org.apache.nifi.processor.io.StreamCallback
import java.nio.charset.StandardCharsets
import com.fasterxml.jackson.databind.ObjectMapper
import java.io.InputStream

def flowFile = session.get()
if (!flowFile) return

def correlationId = flowFile.getAttribute("correlation.id")

// Replace with your actual MapCacheClientService ID
def cacheServiceId = "e9b33431-0195-1000-e188-1fc4e8390004"
def mapCache = context.controllerServiceLookup.getControllerService(cacheServiceId)

def mapper = new ObjectMapper()

try {
    // Step 1: Read content from HTTP1
    def content1 = new StringBuilder()
    flowFile = session.write(flowFile, { inputStream, outputStream ->
        inputStream.eachLine { content1.append(it) }
    } as StreamCallback)

    if (content1.toString().trim().isEmpty()) {
        log.warn("⚠️ FlowFile content from HTTP1 is empty for correlationId=${correlationId}")
    }

    def json1 = mapper.readTree(content1.toString())

    // Step 2: Read cached JSON from HTTP2
    def cachedJson = mapCache.get(
        correlationId + "_data",
        { key, out -> out.write(key.bytes) }, // Serializer
        { inStream ->
            def content
            if (inStream instanceof InputStream) {
                content = inStream.text
            } else if (inStream instanceof byte[]) {
                content = new String(inStream, StandardCharsets.UTF_8)
            } else {
                throw new IOException("Unsupported input type for cache value: ${inStream?.getClass()?.name}")
            }
            return content
        }
    )

    if (cachedJson == null || cachedJson.trim().isEmpty()) {
        log.error("❌ No cached value found for correlationId=${correlationId}_data or value is empty.")
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    def json2 = mapper.readTree(cachedJson)

    // Step 3: Combine both JSONs
    def combined = [
        "responseFromHTTP1": json1,
        "responseFromHTTP2": json2
    ]

    def combinedJsonStr = mapper.writeValueAsString(combined)
    log.info("✅ Combined JSON for correlationId=${correlationId}: ${combinedJsonStr}")

    // Step 4: Write combined content to FlowFile
    flowFile = session.write(flowFile, { _, out ->
        out.write(combinedJsonStr.getBytes(StandardCharsets.UTF_8))
    } as StreamCallback)

    flowFile = session.putAttribute(flowFile, "merged.result", "success")
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("🔥 Groovy script failed for correlationId=${correlationId}: ${e.message}", e)
    session.transfer(flowFile, REL_FAILURE)
}
